function loginActive(){userEl.value.length>=1&&passEl.value.length>=1?(loginBtn.className="btn btn-primary form-control",loginBtn.removeAttribute("disabled")):(loginBtn.className="btn btn-secondary form-control",loginBtn.setAttribute("disabled","disabled"))}const userEl=document.getElementById("email"),passEl=document.getElementById("password"),loginBtn=document.getElementById("loginButton");userEl.addEventListener("keyup",loginActive),passEl.addEventListener("keyup",loginActive),loginBtn.addEventListener("keyup",loginActive);